rule.example:

// Valid
test($arg, $arg2);
// All invalid
test ();
test( );
test() ;
test( $arg);
test( $arg );
test ( $arg );

// No error for multi line calls
test(
    $a,
    $b
);

// Heredocs don't need to be indented.
test(
<<<EOH
Anyone want to recomment parse errors?

EOH
);


// Valid examples:
function foo($a, $b, $c);

function test(
    $a,
    $b
) {
}

function &testFunction($arg1,
    $arg2,
) {
}

function validateUrl(
    $url,
    $requireScheme=TRUE,
    array $allowedSchemes=array(
                           'http',
                           'https',
                          )
) {
}


// Valid
function myFunction($arg1, $arg2='hello')
{
}
// Invalid
function myFunction($arg2='hello', $arg1)
{
}
