rule.example:

class foo {


    public function bar() {

    }


    public function baz() {

    }


}


// Forbidden
echo"hi";
echo   "hi";
$x = new   StdClass();
// Allowed
echo "hi";
$x = new StdClass();


// Forbidden
if($x&& $y) {}
if($x &&$y) {}
if($x &&   $y) {}
if($x   && $y) {}
// Allowed
if($x && $y) {}


// Forbidden
class foo {
    private $foo;
    private $bar;
}
// Allowed
class foo {
    private $foo;

    private $bar;
}
// Also allowed
class foo {
    private $foo;

    /**
     * @var string
     */
    private $bar;
}


// Forbidden
$x = new StdClass();
$x -> foo = 1;
// Allowed
$x = new StdClass();
$x->foo = 1;
echo "foo";


// Forbidden
x = {
    a:  'x',
    b : 1,
    c :  2,
    }
// Allowed
x = {
    a: 'x',
    b:  1,
    c: 2,
}


// Forbidden
function foo() {
   }
function foo() {
    echo "bar"; }
// Allowed
function foo() {
}


// Forbidden
public    function
protected  function
// Allowed
public function
protected function
